5 5. Define Permutation Function

permute_diff &lt;- function(data) {
  shuffled &lt;- sample(data$made_tourney)
  perm_means &lt;- data |&gt; 
    mutate(shuffled_group = shuffled) |&gt; 
    group_by(shuffled_group) |&gt; 
    summarize(mean_ftr = mean(ftr, na.rm = TRUE), .groups = "drop")

  if (!all(c("yes", "no") %in% perm_means$shuffled_group)) {
    return(NA_real_)
  }

  yes_mean &lt;- perm_means |&gt; filter(shuffled_group == "yes") |&gt; pull(mean_ftr)
  no_mean &lt;- perm_means |&gt; filter(shuffled_group == "no") |&gt; pull(mean_ftr)

  return(yes_mean - no_mean)
}

